import NextImage from 'next/image'

interface ImageProps extends React.ImgHTMLAttributes<HTMLImageElement> {
  src: string;
  alt: string;
}

export function OptimizedImage({ src, alt, ...props }: ImageProps) {
  return (
    <NextImage
      src={src}
      alt={alt}
      quality={90}
      loading="lazy"
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64," 
      {...props}
    />
  )
} 